var fs=require("fs"),
	newC=process.argv[2];
fs.readFile("style.css",function(err,data){
	if(err)throw err;

	var oldC=data.toString().split(":")[1].split(";")[0];
	var hh=data.toString().replace(oldC,newC);
	
	fs.writeFile("style.css",hh,{},function(err){
		if(err) throw err; 
	})
	fs.readFile("style.css",function(err,data){
		console.log(data.toString());
	})
})
